mapper.insert:
INSERT INTO SM_CLIP_TB
(
	CLIP_SQ,
	USER_ID,
	CONTENT_ID,
	CONTENT_TYPE_ID,			
	CLIP_YMD
	
)
VALUES
(
	SM_CLIP_TB_CLIP_SQ.nextval
	, #{USER_ID}
	, #{CONTENT_ID}
	, #{CONTENT_TYPE_ID}			
	, sysdate
	
)

mapper.delete:
delete from SM_CLIP_TB
where 		CONTENT_ID =#{CONTENT_ID}
		and USER_ID=#{USER_ID}

