mapper.select:
select count(*)
from SM_REPLY_TB
where SCD_SQ = #{SCD_SQ}

mapper.update:
update
	SM_REPLY_TB
	set 
	REPLY_TXT = #{REPLY_TXT}	
	,UPD_YMD = sysdate
where 
	REPLY_SQ=#{REPLY_SQ}
and	
	SCD_SQ = #{SCD_SQ}
and 
	USER_ID=#{USER_ID}

mapper.delete:
delete
	from
		SM_REPLY_TB
	where
		REPLY_SQ=#{REPLY_SQ}
	and	
		SCD_SQ = #{SCD_SQ}
	and 
		USER_ID=#{USER_ID}
