mapper.select:
select c.user_id, c.scd_sq, c.scd_desc, c.scd_title, q as DTL_CONTENT_ID, w as DTL_CONTENT_ID2 from (select scd_sq,
area_code ,
max(DTL_CONTENT_ID) q, min(DTL_CONTENT_ID) w from sm_dtl_scd_tb group by scd_sq
,area_code ) d, sm_scd_tb c where d.scd_sq=c.scd_sq and public_fl = 'public'
and c.scd_theme=#{schedule.scd_theme}
and c.scd_season=#{schedule.scd_season}
and c.day_cnt =#{schedule.day_cnt}
and c.day_cnt >= 7
and area_code=#{area_code}
order by c.scd_sq desc

select count(*) from (select scd_sq
,area_code
from sm_dtl_scd_tb group by scd_sq
,area_code ) d, sm_scd_tb c where d.scd_sq=c.scd_sq and public_fl = 'public'
and c.scd_theme=#{schedule.scd_theme}
and c.scd_season=#{schedule.scd_season}
and c.day_cnt =#{schedule.day_cnt}
and c.day_cnt >= 7
and area_code=#{area_code}
